import { React, TextInput } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { TouchableOpacity } from 'react-native';

import { useNavigation } from '@react-navigation/native';


export default function Criar() {
    const navigation = useNavigation();

    const [title, setTitle] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");

    return (
        <>
        <View style={styles.container}>
            <View style={styles.conteudo}>
                
                {/* DIV TÍTULO */}
                <View style={styles.title}>
                    <Text style={{fontSize:25}}>Título</Text>
                    <TextInput placeholder='  Minha Bela Imagem' style={styles.inputs}></TextInput>
                </View>
                
                {/* DIV IMAGEM */}
                <View style={styles.image}>
                    <Text style={{fontSize:25}}>URL da imagem</Text>
                    <TextInput placeholder="  https://www.google.com" style={styles.inputs}></TextInput>
                </View>
                
                {/* DIV DESCRIÇÃO */}
                <View style={styles.desc}>
                    <Text style={{fontSize:25}}>Descrição</Text>
                    <TextInput style={styles.inputDesc}></TextInput>
                </View>
            </View>

                {/* BOTAÕ ADICIONAR */}
                <TouchableOpacity style={styles.add} onPress={() => {navigation.goBack() ;}}>
                    <Text style={{fontSize:20 }}>Salvar</Text>
                </TouchableOpacity>

            <StatusBar style="auto" />
        </View>
        </>
    
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',

        backgroundColor: '#fff',

        margin: 0,
        padding: 0,
    },
    conteudo: {
        marginTop: 31,

    },
    add: {
        backgroundColor: '#e8eaed',
        textAlign: 'center',
        justifyContent: 'center',
        alignItems: 'center',

        paddingLeft: 49,
        paddingRight: 49,
        paddingTop: 15,
        paddingBottom: 15,

        borderRadius: 50,
    },
    aviso: {
        fontStyle: 'normal',
        fontWeight: '100',
        fontSize: 18,
        paddingBottom: 33,
        lineHeight: 29.3,

        textAlign: 'center',

        color: "#201E1E",
    },
    inputs: {
        borderWidth: 1,
        borderColor: "#BBBB",

        width: 364,
        height: 40,
        borderRadius: 15,
    },
    inputDesc: {
        borderWidth: 1,
        borderColor: "#BBBB",
        width: 364,
        height: 309,
        borderRadius: 15,
    },
    image:{
        paddingTop: 23 


    },
    desc:{
        paddingTop: 23,
        paddingBottom: 54
    },
});
